const queryString=window.location.search,urlParams=new URLSearchParams(queryString);function save(){var e=firebase.auth().currentUser,a=firebase.storage().ref("/"+e.uid+"/").child(urlParams.get("note")+".txt"),t=btoa(document.getElementsByTagName("textarea")[0].value);a.putString(t).then(e=>{console.log("Updated")}).catch(e=>{console.log(e.message)})}firebase.auth().onAuthStateChanged((function(e){if(e){e=firebase.auth().currentUser;firebase.storage().ref("/"+e.uid+"/").child(urlParams.get("note")+".txt").getDownloadURL().then(e=>{$.ajax({url:e,success:function(e){document.getElementsByTagName("textarea")[0].value=atob(e)}})}).catch(e=>{alert(e.message)})}}));