function openEditor(t){window.location.href="/editor.html?note="+btoa(t.innerText)}async function create(){try{var t=window.prompt("Enter your note name"),e=btoa(t),o=firebase.auth().currentUser,n=firebase.storage().ref(o.uid).child(e+".txt"),a=btoa("This is the beginning of your note");await n.putString(a).then(t=>{console.log("Updated")}).catch(t=>{console.log(t.message)}),location.reload()}catch{}}function createElememt(t){var e='<button class="btn btn-primary" type="button" style="width: 100%;margin-bottom: 20px;" onclick="openEditor(this);">'+t+"</button>";$(document.getElementsByClassName("container")[0]).append(e)}firebase.auth().onAuthStateChanged((function(t){if(t){var e=firebase.storage().ref(t.uid);console.log(t.uid),e.listAll().then(t=>{t.items.forEach(t=>{try{var e=t.name.split(".")[0];createElememt(atob(e))}catch{}})}).catch(t=>{alert(t)})}}));